INSERT INTO TEST_Courses(CourseID,Course,Title,MinCreditHours,MaxCreditHours,CourseDescription,GradeType,Course_type,Department_Description,Subject) VALUES (283,N'B303',N'Critical Social Theories',3,NULL,N'This course is an introduction to the critical social theories that have developed since the late 19th century. Critical social thought in philosophy focuses on various socio-historical conditions in contemporary society, as well as on the sociocultural methods that are used to study them. Possible frameworks may include Marxism, Neo-Marxism, feminist philosophies, queer theory, critical race theory, and post-analytic philosophy. Social and cultural topics may include race, gender, global economic dynamics, Internet culture, and social media.',N'Standard Letter',N'Lecture',N'USC-B Humanities & Social Sci',N'PHIL');
