INSERT INTO TEST_Courses(CourseID,Course,Title,MinCreditHours,MaxCreditHours,CourseDescription,GradeType,Course_type,Department_Description,Subject) VALUES (861,N'B100',N'Nursing Honors Seminar',1,NULL,N'(Prerequisite: Acceptance to the BSN Honors Program) This honors course is designed to provide students the opportunity to explore the changing role of nursing in the American healthcare system. Seminar meetings and planned excursions will facilitate scholarly exploration of the practice of nursing, with an emphasis on the factors influencing current and future nursing practice.',N'Standard Letter',N'Lecture',N'USC-B Nursing',N'NURS');
INSERT INTO TEST_Courses(CourseID,Course,Title,MinCreditHours,MaxCreditHours,CourseDescription,GradeType,Course_type,Department_Description,Subject) VALUES (862,N'B101',N'Nursing Honors Seminar II',1,NULL,N'(Prerequisite: NURS B100) This honors course is designed to provide students with the opportunity to explore the changing role of nursing in the American healthcare system. Seminar meeting and planned excursions will facilitate scholarly exploration of the scope of practice including direct care roles and indirect care roles of the nurse and their influence on health policy and the health of populations across the nation.',N'Standard Letter',N'Lecture',N'USC-B Nursing',N'NURS');
