INSERT INTO TEST_Courses(CourseID,Course,Title,MinCreditHours,MaxCreditHours,CourseDescription,GradeType,Course_type,Department_Description,Subject) VALUES (918,N'B315',N'Philosophy of Technology',3,NULL,N'This course will focus on the various philosophical questions and issues that arise from the technological opportunities and challenges of the 21st century. Philosophical topics surrounding technology will include those related to ethics, the philosophy of language, feminism, critical theory, and pragmatism. Possible topics regarding technology may include information technologies, robotics, AI, genetic engineering, cyborgs, climate change/geoengineering/the Anthropocene, and social media technologies. Finally, we investigate how technological developments stimulate philosophical thinking.',N'Standard Letter',N'Lecture',N'USC-B Humanities & Social Sci',N'PHIL');
