INSERT INTO TEST_Courses(CourseID,Course,Title,MinCreditHours,MaxCreditHours,CourseDescription,GradeType,Course_type,Department_Description,Subject) VALUES (1205,N'B330',N'Transcultural Health Practices',3,NULL,N'(Prerequisite: PSYC B101 or SOCY B101 or consent of instructor) This course focuses on the health practices of individual, family and communities with varied cultural, socioeconomic, and global backgrounds. Cultural variables such as age, gender, ethnicity, family structure, spiritual practice and religion, sexual expression, economics, and common health practices are examined to sensitize providers to person-centered care. Cultural practices in birth, health, pain management, natural healing practices, illness and death are explained and reinforced.',N'Standard Letter',N'Lecture',N'USC-B Nursing',N'NURS');
