INSERT INTO TEST_Courses(CourseID,Course,Title,MinCreditHours,MaxCreditHours,CourseDescription,GradeType,Course_type,Department_Description,Subject) VALUES (1243,N'B315',N'Wrd Pop: Problems and Policies',3,NULL,N'World population growth and concomitant socioeconomic problems. Effectiveness of governmental policies concerned with population growth. Topics include over-population. (Satisfies USCB General Education Global Citizenship and Multicultural Understanding Requirement).',N'Standard Letter',N'Lecture',N'USC-B Humanities & Social Sci',N'SOCY');
INSERT INTO TEST_Courses(CourseID,Course,Title,MinCreditHours,MaxCreditHours,CourseDescription,GradeType,Course_type,Department_Description,Subject) VALUES (1244,N'B210',N'Writing Across Media Platforms',3,NULL,N'(Prerequisite: ENGL B101 or B105, and B102 or B106, each with a grade of C or higher, and COMM B140 and COMM B190) Students will learn practical skills to help them succeed in writing for specifically online news environments. Emphasis is given to clear and effective communication and professional standards as well as the need to tailor the message to the audience and the platform through which it is communicated.',N'Standard Letter',N'Lecture',N'USC-B Business & Comm',N'COMM');
INSERT INTO TEST_Courses(CourseID,Course,Title,MinCreditHours,MaxCreditHours,CourseDescription,GradeType,Course_type,Department_Description,Subject) VALUES (1245,N'B626',N'Writing Across the Curriculum',3,NULL,N'This course is designed to instruct graduate candidates, grades k-12 in developing methods and materials that will improve their students  literacy skills in the content areas of math, social studies, science, and health. Emphasis is on the application of strategies that will support students in reading, writing, and working with media in the context of content area instruction.',N'Standard Letter',N'Lecture',N'USC-B Education',N'EDRD');
