INSERT INTO TEST_Courses(CourseID,Course,Title,MinCreditHours,MaxCreditHours,CourseDescription,GradeType,Course_type,Department_Description,Subject) VALUES (1252,N'B320',N'Yoga Disciplines & Practice',3,NULL,N'(Prerequisite: Sophomore Standing) A broad introduction to the physical and philosophical practice of yoga. Examination of historical yogic texts and application of ideas in modern society via community outreach component.',N'Standard Letter',N'Lecture',N'USC-B English & Visual Arts',N'IDST');
